import { useRef } from "react";

const scrollHandler = function () {
  const myRef = React.useRef();
  const scroll = (ref) =>
    ref &&
    myRef.current &&
    myRef.current.scrollIntoView({ behavior: "smooth" });
  return (
    <>
      <button onClick={scroll}>Scroll down</button>
    </>
  );
};
export default scrollHandler;
