import { useRef } from "react";

const ScrollHandler = function () {
  const myRef = useRef();
  const scroll = (ref) =>
    myRef.current.scrollIntoView({
      behavior: "smooth",
      block: "start",
      inline: "nearest",
    });
  return (
    <>
      <button onClick={scroll}>Scroll down</button>
    </>
  );
};
export default ScrollHandler;
