import { useRef } from "react";
import React from "react";

function ScrollTo() {
  React.createRef(myRef);
  const scroll = (ref) =>
    ref &&
    myRef.current &&
    myRef.current.scrollIntoView({ behavior: "smooth" });
  return (
    <>
      <button onClick={scroll}>Scroll down</button>
    </>
  );
}
export default ScrollTo;
