import { useRef } from "react";
import Services from "../components/services/Services";

function ScrollTo() {
  const myRef = useRef();
  const scroll = (ref) =>
    ref &&
    myRef.current &&
    myRef.current.scrollIntoView({ behavior: "smooth" });
  return (
    <div>
      <button onClick={scroll}>Scroll down</button>
    </div>
  );
}
export default ScrollTo;
